import { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

export default function M() {
  const [text, setText] = useState('');
  const fullText = "I am a skilled software developer specializing in web development and Java programming. With expertise in MERN stack and WordPress, I have successfully delivered a variety of online projects. My passion lies in crafting efficient and user-friendly solutions that leverage cutting-edge technologies to solve complex challenges.";

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      setText(fullText.slice(0, index));
      index++;
      if (index > fullText.length) {
        clearInterval(interval);
      }
    }, 50);
    return () => clearInterval(interval);
  }, []);

  return (
    <div
      className="relative w-full min-h-screen bg-white"
      style={{
        backgroundImage: "url('./pexels-alex-andrews-271121-861443.png')",
        backgroundSize: "cover",
        backgroundPosition: "top",
        backgroundRepeat: "repeat-y",
      }}
    >
      <div className="flex flex-col md:flex-row">
        {/* Image Section */}
        <motion.div
          className="relative order-1 md:order-2 flex w-full h-64 md:h-screen opacity-70"
          style={{
            backgroundImage: "url('./image1.png')",
            backgroundSize: "cover",
            backgroundPosition: "top",
            backgroundRepeat: "no-repeat",
          }}
          initial={{ opacity: 0, scale: 1 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1, delay: 0 }}
        >
          <div className="flex items-center justify-center w-full h-full">
            <a className="text-4xl md:text-7xl font-black">
              <span className="text-white">M </span>
              <span className="text-blue-500">Jay</span>
            </a>
          </div>
        </motion.div>

        {/* Text Section */}
        <motion.div
          className="order-2 md:order-1 flex flex-col items-start p-6 md:p-20 w-full md:w-1/3"
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1 }}
        >
          <div className="text-black font-black text-4xl md:text-8xl">
            software <br /> engineer
          </div>
          <motion.div
            className="mt-10 md:mt-40 text-justify"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 2, delay: 0.5 }}
          >
            <p className="mt-10 text-base md:text-lg bg-gradient-to-t from-blue-700 to-black bg-clip-text text-transparent">
              {text}
            </p>
          </motion.div>
        </motion.div>

        {/* Vertical Text Section */}
        <motion.div
          className="order-3 flex items-center justify-center bg-gray-200 w-full md:w-1/5 text-4xl md:text-8xl font-black"
          initial={{ x: 50, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <p className="rotate-0 md:-rotate-90 mt-10 md:mt-96">
            <span className="text-white">mihisara</span> jaysinghe
          </p>
        </motion.div>
      </div>

      {/* Navigation Section */}
      <motion.div
        className="flex flex-wrap md:flex-nowrap absolute bottom-0 md:top-[750px] text-base md:text-lg bg-gray-200 h-16 md:h-24 w-full text-xl md:text-4xl font-black justify-around md:justify-start pt-3 md:pt-5"
        initial={{ y: 50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 1, delay: 1 }}
      >
        <nav className="pl-0 md:pl-20 text-gray-600">ui./ux</nav>
        <nav className="pl-0 md:pl-20">full stack</nav>
        <nav className="pl-0 md:pl-20 text-white">ui./ux</nav>
        <nav className="pl-0 md:pl-20">mobile</nav>
        <nav className="pl-0 md:pl-20">ui./ux</nav>
      </motion.div>

      <div className='h-96'>
        kalfaskaskdaskdaaaskm
      </div>
    </div>
  );
}
