import { useState, useEffect } from 'react';
import { useScroll, useTransform } from 'framer-motion';
import LoadingScreen from './Loading';
import Header from './ Head ';

import SideName from './ Sidename';

import SkillsGrid from './ SkillsGurd';
import Projects from './components/Projects';

const Main = () => {
  const [loading, setLoading] = useState(true);
  const [text, setText] = useState("");
  const { scrollY } = useScroll();
  const scale = useTransform(scrollY, [0, 700], [0, 1]);
  const opacity = useTransform(scrollY, [0, 900], [0, 1]);

  const fullText = "I am a skilled software developer specializing in web development and Java programming. With expertise in MERN stack";

  // Simulate loading delay
  useEffect(() => {
    const timeout = setTimeout(() => {
      setLoading(false);
      setText(fullText);  // Update text after loading
    }, 3000);

    return () => clearTimeout(timeout);
  }, []);

  if (loading) {
    return <LoadingScreen text="Loading..." />;
  }

  return (
    <div className="min-h-screen">
      <Header />
      <MainSection text={text} />
      <SideName />
      <FooterNav />
      <SkillsGrid opacity={opacity} scale={scale} />
      <Projects />
    </div>
  );
};

export default Main;
