import { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

export default function M() {

  const [text, setText] = useState('');
  const fullText = "I am a skilled software developer specializing in web development and Java programming. With expertise in MERN stack and WordPress, I have successfully delivered a variety of online projects. My passion lies in crafting efficient and user-friendly solutions that leverage cutting-edge technologies to solve complex challenges.";

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      setText(fullText.slice(0, index));
      index++;
      if (index > fullText.length) {
        clearInterval(interval);
      }
    }, 50);
    return () => clearInterval(interval);
  }, []);

  return (
    <div
      className="relative w-full min-h-screen bg-white bg-cover bg-top bg-repeat-y"
      style={{
        backgroundImage: "url('./pexels-alex-andrews-271121-861443.png')",
      }}
    >
      <div className="flex flex-col md:flex-row">
        <motion.div 
          className="flex flex-col md:w-1/3 p-5"
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1 }}
        >
          <div className="text-black font-black mt-10 md:mt-20 text-4xl md:text-8xl">
            software <br />engineer
          </div>
          <motion.div 
            className="mt-10 md:mt-40 text-justify"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 2, delay: 0.5 }}
          >
            <p className="mt-5 md:mt-20 text-lg bg-gradient-to-t from-blue-700 to-black bg-clip-text text-transparent">
              {text}
            </p>
          </motion.div>
        </motion.div>

        <motion.div
          className="relative mt-5 md:mt-15 flex w-full h-60 md:h-screen opacity-70"
          style={{
            backgroundImage: "url('./image1.png')",
            backgroundSize: "cover",
            backgroundPosition: "top",
            backgroundRepeat: "no-repeat",
          }}
          initial={{ opacity: 0, scale: 1 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1, delay: 0 }}
        >
          <div className="text-3xl md:text-7xl font-black mt-40 md:mt-[620px] ml-10 md:ml-56">
            <span className="text-white">M </span> 
            <span className="text-blue-500">Jay</span>
          </div>
        </motion.div>

        <motion.div 
          className="bg-gray-200 text-4xl md:text-8xl font-black md:w-1/5 p-5"
          initial={{ x: 50, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <p className="md:-rotate-90 mt-10 md:mt-96">
            <span className="text-white">mihisara</span> jaysinghe
          </p>
        </motion.div>
      </div>

      <motion.div 
        className="inline-flex absolute bottom-0 left-0 right-0 text-lg bg-gray-200 h-24 w-full text-xl md:text-4xl font-black justify-between items-center p-5 md:p-10"
        initial={{ y: 50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 1, delay: 1 }}
      >
        <nav className="text-gray-600">ui./ux</nav>
        <nav>full stack</nav>
        <nav className="text-white">ui./ux</nav>
        <nav>mobile</nav>
        <nav>ui./ux</nav>
      </motion.div>

      <div className='h-96'>
        kalfaskaskdaskdaaaskm
      </div>
    </div>
  );
}
