import { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { useScroll, useTransform } from "framer-motion";
import { FaRocket, FaSchool, FaAndroid, FaHeartbeat, FaShieldAlt } from "react-icons/fa";
import ContactMe from "./Mailcontact";

export default function M() {
  const [loading, setLoading] = useState(true);
  const [text, setText] = useState("");
  const { scrollY } = useScroll();
  const scale = useTransform(scrollY, [0, 700], [0, 1]);
  const opacity = useTransform(scrollY, [0, 900], [0, 1]);

  const fullText =
    "I am a skilled software developer specializing in web development and Java programming. With expertise in MERN stack and WordPress, I have successfully delivered a variety of online projects. My passion lies in crafting efficient and user-friendly solutions that leverage cutting-edge technologies to solve complex challenges.";

  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 2000);

    let index = 0;
    const interval = setInterval(() => {
      setText(fullText.slice(0, index));
      index++;
      if (index > fullText.length) {
        clearInterval(interval);
      }
    }, 10);

    return () => {
      clearTimeout(timer);
      clearInterval(interval);
    };
  }, []);

  return (
    <>
      {loading ? (
        <div className="flex justify-center items-center text-left w-3/4 ml-auto mr-auto h-screen text-black text-7xl">
          <div className="loader">
            <a>Loading</a>
            <p className="mt-20 text-md font-thin bg-gradient-to-t from-blue-700 to-gray-600 bg-clip-text text-transparent">
              {text}
            </p>
          </div>
        </div>
      ) : (
        <>
          <div className="relative w-full min-h-screen -white">
            <div className="pl-4 md:pl-20 z-30 pt-5 pb-5 sticky inline-flex top-0 backdrop-blur-lg font-black bg-300 w-full">
              <nav className="pl-2">home</nav>
              <nav className="pl-2">about</nav>
            </div>
            <div className="flex flex-col xl:flex-row h-full">
              <motion.div
                className="flex-col w-full xl:w-1/3 p-8 md:p-20"
                initial={{ opacity: 0, x: -50 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 1 }}
              >
                <div className="text-blue-500 font-black font-helvetica text-4xl md:text-8xl">
                  Software <br /> Engineer
                </div>
                <motion.div
                  className="mt-8 md:mt-20 text-justify"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ duration: 2, delay: 0.5 }}
                >
                  <p className="mt-8 text-sm md:text-md font-thin bg-gradient-to-t from-blue-700 to-gray-600 bg-clip-text text-transparent">
                    {text}
                  </p>
                </motion.div>
              </motion.div>

              <motion.div
                className="z-40 mt-8 md:mt-15 flex w-[300px] md:w-[600px] h-[400px] md:h-[800px] pb-10 md:pb-20 opacity-70 mx-auto"
                style={{
                  backgroundImage: "url('./image1.png')",
                  backgroundSize: "cover",
                  opacity: "80%",
                  backgroundPosition: "center",
                  backgroundRepeat: "no-repeat",
                }}
                initial={{ opacity: 0, scale: 1 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ duration: 1, delay: 0 }}
              >
                <a className="text-2xl md:text-3xl font-black absolute bottom-0 left-16 md:top-[600px] md:left-64">
                  <span className="text-white">M </span>
                  <span className="text-blue-500">Jay</span>
                </a>
              </motion.div>

              <motion.div
                className="bg-black z-40 text-3xl md:text-7xl fixed right-0 top-0 bottom-0 font-black w-1/5 hidden xl:block"
                initial={{ x: 50, opacity: 0 }}
                animate={{ x: 0, opacity: 1 }}
                transition={{ duration: 1 }}
              >
                <p className="-rotate-90 text-blue-500 mt-40 md:mt-96">
                  <span className="text-white"> Mihisara</span> Jayasinghe
                </p>
              </motion.div>

              <motion.div
                className="inline-flex z-50 fixed bottom-0 text-blue-500 text-sm md:text-xl bg-black h-10 w-full font-black justify-left pt-1"
                initial={{ y: 50, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ duration: 1, delay: 1 }}
              >
                <nav className="pl-4 text-gray-600">UI/UX</nav>
                <nav className="pl-4">Full Stack</nav>
                <nav className="pl-4 text-white">UI/UX</nav>
                <nav className="pl-4">Mobile</nav>
                <nav className="pl-4">UI/UX</nav>
              </motion.div>
            </div>

            <motion.div
              className="backdrop-blur-md h-screen pt-40"
              style={{ opacity, scale }}
            >
              <div className="grid ml-10 md:ml-20 grid-cols-2 md:grid-cols-4 text-lg md:text-4xl justify-center font-black gap-4 md:gap-10 pr-10 md:pr-96 text-center">
                <div className="p-4 text-white h-48 backdrop-blur-sm bg-gray-900 bg-opacity-10 rounded-lg shadow-md animate-colorChange1">
