import { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

export default function M() {
  const [text, setText] = useState('');
  const fullText = "I am a skilled software developer specializing in web development and Java programming. With expertise in MERN stack and WordPress, I have successfully delivered a variety of online projects. My passion lies in crafting efficient and user-friendly solutions that leverage cutting-edge technologies to solve complex challenges.";

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      setText(fullText.slice(0, index));
      index++;
      if (index > fullText.length) {
        clearInterval(interval);
      }
    }, 50);
    return () => clearInterval(interval);
  }, []);

  return (
    <div
      className="relative w-full min-h-screen bg-white"
      style={{
        backgroundImage: "url('./pexels-alex-andrews-271121-861443.png')",
        backgroundSize: "cover",
        backgroundPosition: "top",
        backgroundRepeat: "repeat-y",
      }}
    >
      <div className="flex flex-col md:flex-row">
        {/* Main Content for Desktop and Mobile */}
        <motion.div 
          className="flex-col w-full md:w-1/3 p-4 md:p-8"
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1 }}
        >
          <div className="text-black font-black text-4xl md:text-6xl lg:text-8xl mb-4 md:mb-8">
            Software <br /> Engineer <br /> <span className="text-blue-500">M Jay</span>
          </div>
          <motion.div 
            className="text-justify"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 2, delay: 0.5 }}
          >
            <p className="text-base md:text-lg bg-gradient-to-t from-blue-700 to-black bg-clip-text text-transparent">
              {text}
            </p>
          </motion.div>
        </motion.div>

        {/* Sidebar for Mobile Only */}
        <div className="absolute top-0 right-0 hidden md:block w-1/5 bg-gray-200 text-center rotate-90 origin-top-right transform -translate-x-1/2">
          <p className="text-xl md:text-2xl lg:text-4xl font-black text-gray-700">
            Mihisara Jayasinghe
          </p>
        </div>

        {/* Image Section */}
        <motion.div
          className="relative mt-8 w-full h-64 md:h-screen opacity-70"
          style={{
            backgroundImage: "url('./image1.png')",
            backgroundSize: "cover",
            backgroundPosition: "top",
            backgroundRepeat: "no-repeat",
          }}
          initial={{ opacity: 0, scale: 1 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1, delay: 0 }}
        >
          <a className="text-4xl md:text-7xl font-black absolute bottom-8 left-8 md:bottom-[620px] md:left-[56px]">
            <span className="text-white">M </span>
            <span className="text-blue-500">Jay</span>
          </a>
        </motion.div>
      </div>

      <motion.div 
        className="inline-flex absolute bottom-0 w-full text-base md:text-lg bg-gray-200 h-24 text-2xl font-black items-center justify-center space-x-4 pt-5"
        initial={{ y: 50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 1, delay: 1 }}
      >
        <nav className="text-gray-600">UI/UX</nav>
        <nav>Full Stack</nav>
        <nav className="text-white">UI/UX</nav>
        <nav>Mobile</nav>
        <nav>UI/UX</nav>
      </motion.div>

      <div className='h-96'>
        kalfaskaskdaskdaaaskm
      </div>
    </div>
  );
}
