import { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

export default function M() {
  const [text, setText] = useState('');
  const fullText = "I am a skilled software developer specializing in web development and Java programming. With expertise in MERN stack and WordPress, I have successfully delivered a variety of online projects. My passion lies in crafting efficient and user-friendly solutions that leverage cutting-edge technologies to solve complex challenges.";

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      setText(fullText.slice(0, index));
      index++;
      if (index > fullText.length) {
        clearInterval(interval);
      }
    }, 50);
    return () => clearInterval(interval);
  }, []);

  return (
    <div
      className="relative w-full min-h-screen bg-white"
      style={{
        backgroundImage: "url('./pexels-alex-andrews-271121-861443.png')",
        backgroundSize: "cover",
        backgroundPosition: "top",
        backgroundRepeat: "no-repeat",
      }}
    >
      <div className="flex flex-col items-center justify-center px-4 py-8 md:flex-row md:items-start md:justify-start">
        <motion.div 
          className="w-full md:w-1/3 mb-8 md:mb-0"
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1 }}
        >
          <div className="text-black font-black text-4xl md:text-6xl lg:text-8xl">
            Software <br /> Engineer
          </div>
          <motion.div 
            className="mt-8 text-justify"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 2, delay: 0.5 }}
          >
            <p className="text-lg bg-gradient-to-t from-blue-700 to-black bg-clip-text text-transparent">
              {text}
            </p>
          </motion.div>
        </motion.div>

        <motion.div
          className="relative mb-8 w-full h-64 bg-cover bg-center bg-no-repeat opacity-80 md:h-screen"
          style={{
            backgroundImage: "url('./image1.png')",
          }}
          initial={{ opacity: 0, scale: 1 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1 }}
        >
          <a className="text-4xl font-black absolute bottom-8 left-8 md:text-7xl">
            <span className="text-white">M </span>
            <span className="text-blue-500">Jay</span>
          </a>
        </motion.div>

        <motion.div 
          className="bg-gray-200 text-xl md:text-3xl font-black w-full md:w-1/5 text-center"
          initial={{ x: 50, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <p className="rotate-90 mt-32 md:mt-96">
            <span className="text-white">mihisara</span> jaysinghe
          </p>
        </motion.div>
      </div>

      <motion.div 
        className="absolute bottom-0 w-full text-lg bg-gray-200 h-24 flex items-center justify-center space-x-4 text-2xl font-black"
        initial={{ y: 50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 1, delay: 1 }}
      >
        <nav className="text-gray-600">UI/UX</nav>
        <nav>Full Stack</nav>
        <nav className="text-white">UI/UX</nav>
        <nav>Mobile</nav>
        <nav>UI/UX</nav>
      </motion.div>

      <div className='h-96'>
        kalfaskaskdaskdaaaskm
      </div>
    </div>
  );
}
