import { useState } from "react";
import { FaEnvelope, FaPhone, FaMapMarkerAlt } from "react-icons/fa";
import emailjs from "emailjs-com";

export default function ContactMe() {
  const [form, setForm] = useState({ name: "", email: "", message: "" });
  const [status, setStatus] = useState(""); // To show success or error messages

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_a86179e', 'template_845r8vm', e.target, 'YOUR_USER_ID')
      .then((result) => {
        console.log(result.text);
        setStatus("Message sent successfully!");
        setForm({ name: "", email: "", message: "" });
      })
      .catch((error) => {
        console.log(error.text);
        setStatus("Failed to send message. Please try again.");
      });
  };

  return (
    <section className="bg-gray-900 text-white py-16 px-4">
      <div className="container pr-40 mx-auto">
        <h2 className="text-4xl font-bold text-center mb-8">Contact Me</h2>
        <div className="flex flex-col md:flex-row justify-center items-center md:items-start gap-12">
          {/* Contact Information */}
          <div className="flex flex-col items-center md:items-start">
            <FaEnvelope className="text-3xl mb-4 text-blue-500" />
            <p className="text-lg">pasanmihisarajayasinghe@example.com</p>

            <FaMapMarkerAlt className="text-3xl mt-6 mb-4 text-blue-500" />
            <p className="text-lg">Colombo, Sri Lanka</p>
          </div>
          {/* Contact Form */}
          <form
            className="w-full max-w-md bg-gray-800 p-8 rounded-lg shadow-lg"
            onSubmit={handleSubmit}
          >
            <div className="mb-6">
              <label htmlFor="name" className="block text-sm font-medium mb-2">
                Name
              </label>
              <input
                type="text"
                id="name"
                name="name"
                className="w-full p-3 rounded-lg bg-gray-700 text-white border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={form.name}
                onChange={handleChange}
              />
            </div>
            <div className="mb-6">
              <label htmlFor="email" className="block text-sm font-medium mb-2">
                Email
              </label>
              <input
                type="email"
                id="email"
                name="email"
                className="w-full p-3 rounded-lg bg-gray-700 text-white border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={form.email}
                onChange={handleChange}
              />
            </div>
            <div className="mb-6">
              <label htmlFor="message" className="block text-sm font-medium mb-2">
                Message
              </label>
              <textarea
                id="message"
                name="message"
                rows="4"
                className="w-full p-3 rounded-lg bg-gray-700 text-white border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={form.message}
                onChange={handleChange}
              ></textarea>
            </div>
            <button
              type="submit"
              className="w-full bg-blue-500 hover:bg-blue-600 text-white font-bold py-3 rounded-lg shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              Send Message
            </button>
          </form>
          {/* Status Message */}
          {status && (
            <p className={`mt-6 text-lg font-bold ${status.includes("successfully") ? "text-green-500" : "text-red-500"}`}>
              {status}
            </p>
          )}
        </div>
      </div>
    </section>
  );
}
